        //         this.props.loadUser(user)
        //         this.props.loadPersonal(user)
        //         this.props.onRouteChange('home')
        //         console.log(user.name);
        //     }
        // }
        // )


        // console.log(this.state);
        // let user = this.props.user
        // if(user.email === this.state.signInEmail &&
        //     user.password === this.state.signInPassword) {
        //         this.props.routeChange('Dashboard')
        //     }


    }

    render() {

        return(
        <div id='sign'>
            <div className="sign-container">
                <form>                    
                    <h1 style={{fontWeight: 'bold'}}>Sign In</h1>
                    <div className='sign-fields'>
                        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                        <input                         
                        className="pa2 br2 input-reset ba bg-transparent hover-bg-black text-white hover-white w-100" 
                        type="email" 
                        name="email-address"  
                        id="email-address"
                        onChange={(e) => this.onEmailChange(e)}
                        />
                    </div>
                    <div className="sign-fields">
                        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                        <input 
                        type="password" 
                        name="password"  
                        id="password"
                        onChange={(e) => this.onPasswordChange(e)}
                        />
                    </div>
                    
                    <div className='btn'onClick={() => this.onSubmitSignIn()}>Submit</div>
                    
                    
                    <p 
                        // href="#0" 
                        // id='reg'
                        // className='btn'
                        style={{cursor: 'pointer', color: 'white'}}
                        onClick={() => this.props.routeChange('register')}
                        >Register</p>
                    
                </form>
            </div>
        </div>
    )
}
    }

    export default SignIn
