import React, {Component} from 'react';

class Register extends Component{

    constructor(props) {
        super(props)
        this.state = {
            name: '',
            email: '',
            password: '',
            confirmPassword: ''
        }
    }

    onNameChange = (e) => {
        this.setState({name : e.target.value})
    }

    onEmailChange = (e) => {
        this.setState({email : e.target.value})
    }

    onPasswordChange = (e) => {
        this.setState({password : e.target.value})
    }

    onConfirmPasswordChange = (e) => {
        this.setState({password : e.target.value})
    }

    onSubmitRegister = (e) => {
        fetch('http://localhost:3000/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/Json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        .then(res => res.json())
        .then(console.log)
        // .then(
        //     user => {
        //     if(user.id){
        //         this.props.loadUser(user)
        //         this.props.loadPersonal(user)
        //         this.props.onRouteChange('home')
        //         console.log(user.name);
        //     }
        // }
        // )
        let user = this.props.user
        if(user.email === this.state.signInEmail &&
            user.password === this.state.signInPassword) {
                this.props.routeChange('Dashboard')
            }


    }

    render() {

        return(
        <div id='sign'>
            <div className="sign-container">
                <form>                    
                    <h1 style={{fontWeight: 'bold'}}>Register</h1>
                    <div className='sign-fields'>
                        <label htmlFor="name">Name</label>
                        <input                         
                        className="pa2 br2 input-reset ba bg-transparent hover-bg-black text-white hover-white w-100" 
                        type="text" 
                        name="name"  
                        id="name"
                        onChange={(e) => this.onEmailChange(e)}
                        />
                    </div>

                    <div className='sign-fields'>
                        <label htmlFor="email-address">Email</label>
                        <input                         
                        className="pa2 br2 input-reset ba bg-transparent hover-bg-black text-white hover-white w-100" 
                        type="email" 
                        name="email-address"  
                        id="email-address"
                        onChange={(e) => this.onEmailChange(e)}
                        />
                    </div>
                    <div className="sign-fields">
                        <label htmlFor="password">Password</label>
                        <input 
                        type="password" 
                        name="password"  
                        id="password"
                        onChange={(e) => this.onPasswordChange(e)}
                        />
                    </div>

                    <div className="sign-fields">
                        <label htmlFor="confirm-password">Confirm</label>
                        <input 
                        type="password" 
                        name="confirm-password"  
                        id="confirm-password"
                        onChange={(e) => this.onConfirmPasswordChange(e)}
                        />
                    </div>
                    
                    <div className='btn'onClick={() => this.onSubmitRegister()}>Submit</div>
                    
                    
                    <p 
                        href="#0" 
                        // id='reg'
                        // className='btn'
                        style={{cursor: 'pointer', marginTop: '1px', color: 'white'}}
                        onClick={() => this.props.routeChange('signin')}
                        >Back</p>
                    
                </form>
            </div>
        </div>
    )
}
    }

    export default Register
